import UseFetch from "./UseFetch"
import { useParams } from "react-router-dom"

const Filtering = () => {
  const { id } = useParams()
  const { data, isPending, error } = UseFetch("http://localhost:5000/response/")
  // console.log(`Details:`, news)
  const result = data.filter(element => element.sectionId === id);
  return result[0].fields
}

export default Filtering
