      const response = await fetch('https://content.guardianapis.com/search?api-key=4aa45470-7bc3-4465-8327-49285e3bf381');
      // const response = await fetch("https://content.guardianapis.com/world/2021/mar/22/link-between-diabetes-and-coronavirus-infections?show-fields=body&api-key=4aa45470-7bc3-4465-8327-49285e3bf381");
      // console.log(response);
      const data = await response.json();


      return data
    }
    asyncFetch()
      .then(data => {
        console.log(`resolved: `, data);
        // setData(data);
      })
      .catch(err => console.log(err.message));
  })

  return (
    <div className="App">
      {/* </> */}
    </div>
  );
}

export default App;
