import { useParams } from "react-router-dom"
import UseFetch from "./UseFetch"

const NewsDetails = () => {
    const {id} = useParams()
    const { data: news, isPending, error } = UseFetch("http://localhost:5000/response/" + id)
    console.log(`id`,id)
  return (
      <div className="news-details">
          {isPending && <div>Loading...</div>}
          {error && <div>{error}</div>}
          <article>
              <h2>{news.fields.headline}</h2>
              <div>{news.fields.bodyText}</div>

          </article>
    </div>

  )
}

export default NewsDetails
