import UseFetch from "./UseFetch"

const NewsDetails = () => {



    const { id } = useParams()
    const { data: news, isPending, error } = UseFetch("http://localhost:5000/response/")
    console.log(`Details:` ,news)
    // console.log(id)
    // const result = data.filter(element => element.sectionId === id);
    // const finalResult = result[0];
    // // const body = finalResult.fields.bodyText;
    // // const headLine = finalResult.fields.headline;
    // console.log(`finalResult`, finalResult.fields)
    // const result = using map function;
    const result = news.filter(element => element.sectionId === id);
    const outputResult = result.map(element => {
        {  <article>
              <h2>{result[0].fields.headline}</h2>
              <div>{result[0].fields.bodyText}</div>

          </article>} 
    })

  return (
      <div className="news-details">
          {isPending && <div>Loading...</div>}
          {error && <div>{error}</div>}

          <h2>welcome</h2>
    </div>

  )
}

export default NewsDetails
